const scene = new THREE.Scene();
const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 10000);
const renderer = new THREE.WebGLRenderer({ antialias: true });
renderer.setSize(window.innerWidth, window.innerHeight);
document.body.appendChild(renderer.domElement);

// === GAME STATE ===
let score = 0;
const scoreDisplay = document.getElementById('distance-display');
scoreDisplay.style.fontSize = '24px';

function updateScoreDisplay() {
  scoreDisplay.textContent = `Score: ${score} | Distance: ${Math.abs(shuttle.position.z - goal.position.z).toFixed(0)}`;
}

// === AIMING SYSTEM ===
const raycaster = new THREE.Raycaster();
const mouse = new THREE.Vector2(0, 0);
let mouseMoved = false;

const aimPoint = new THREE.Vector3();
const aimPlane = new THREE.Plane(new THREE.Vector3(0, 0, -1), 0);

// Aim line (dotted green line)
const aimLineMaterial = new THREE.LineDashedMaterial({ 
  color: 0x00ff00,
  dashSize: 1,
  gapSize: 0.5,
  linewidth: 2 
});
const aimLineGeometry = new THREE.BufferGeometry().setFromPoints([
  new THREE.Vector3(),
  new THREE.Vector3()
]);
