function createExplosion(position) {
  // Create a simple particle system for the explosion
  const particleCount = 30;
  const geometry = new THREE.BufferGeometry();
  const vertices = [];

  for (let i = 0; i < particleCount; i++) {
    const angle = Math.random() * Math.PI * 2;
    const radius = Math.random() * 3;

    // Create particles in a sphere around the explosion point
    vertices.push(
      position.x + Math.cos(angle) * radius,
      position.y + Math.sin(angle) * radius,
      position.z + (Math.random() - 0.5) * radius
    );
  }

  geometry.setAttribute('position', new THREE.Float32BufferAttribute(vertices, 3));

  const material = new THREE.PointsMaterial({
    color: 0xff8800,
    size: 2,
    transparent: true,
    opacity: 1
  });

  const particles = new THREE.Points(geometry, material);
  scene.add(particles);

  // Add a flash light at explosion point
  const light = new THREE.PointLight(0xff5500, 5, 50);
  light.position.copy(position);
  scene.add(light);

  console.log(`Explosion created at ${position.x.toFixed(2)}, ${position.y.toFixed(2)}, ${position.z.toFixed(2)}`);

  // Animate and remove after 1 second
  let opacity = 1;
  const fadeInterval = setInterval(() => {
    opacity -= 0.05;
    material.opacity = opacity;
    light.intensity = opacity * 5;

    if (opacity <= 0) {
      clearInterval(fadeInterval);
      scene.remove(particles);
      scene.remove(light);
    }
  }, 50);
}

