    const hit = intersects[0].object;
    const parentAsteroid = hit.parent;
    if (parentAsteroid.material?.emissive) {
      parentAsteroid.material.emissive.set(0xff0000);
      highlightedAsteroid = parentAsteroid;
    }
  }
}

