  mouse.y = -(event.clientY / window.innerHeight) * 2 + 1;
  mouseMoved = true;
});

// === MISSILE SYSTEM ===
const missiles = [];
let missileCooldown = false;

function createMissile() {
  const geometry = new THREE.ConeGeometry(0.2, 1, 16);
  geometry.rotateX(Math.PI / 2);
  const material = new THREE.MeshPhongMaterial({
    color: 0xff3333,
    emissive: 0xff0000,
    emissiveIntensity: 0.5
  });
  const missile = new THREE.Mesh(geometry, material);

  const flameGeometry = new THREE.ConeGeometry(0.15, 0.5, 8);
  flameGeometry.rotateX(Math.PI / 2);
  const flameMaterial = new THREE.MeshBasicMaterial({
    color: 0xffaa00,
    transparent: true,
    opacity: 0.8
  });
  const flame = new THREE.Mesh(flameGeometry, flameMaterial);
  flame.position.set(0, 0, -0.6);
  missile.add(flame);

  return missile;
}

function shootMissile() {
  if (missileCooldown || !shuttle) return;
  missileCooldown = true;

  const missile = createMissile();
  missile.position.copy(shuttle.position);
  missile.quaternion.copy(shuttle.quaternion);
  missile.translateZ(2); // Slightly ahead of shuttle

  // Compute velocity toward aimPoint
  const direction = aimPoint.clone().sub(shuttle.position).normalize();

  missile.userData = {
    velocity: direction.clone().multiplyScalar(15),
    lifetime: 0
  };

  scene.add(missile);
  missiles.push(missile);

  setTimeout(() => missileCooldown = false, 300);
}

function updateMissiles(deltaTime) {
  for (let i = missiles.length - 1; i >= 0; i--) {
    const missile = missiles[i];
    missile.position.add(missile.userData.velocity.clone().multiplyScalar(deltaTime));
    missile.userData.lifetime += deltaTime;
    if (missile.userData.lifetime > 3 || missile.position.distanceTo(shuttle.position) > 1000) {
      scene.remove(missile);
      missiles.splice(i, 1);
    }
  }
}

