  ];
  let loaded = 0;
  asteroidPaths.forEach((path, index) => {
    loader.load(path, (gltf) => {
      asteroidModels[index] = gltf.scene;
      loaded++;
      if (loaded === asteroidPaths.length) {
        createAsteroids();
      }
    });
  });
}

function createAsteroids() {
  for (let i = 0; i < numAsteroids; i++) {
    const asteroid = asteroidModels[Math.floor(Math.random() * asteroidModels.length)].clone();
    asteroid.position.set(
      (Math.random() - 0.5) * 5000,
      (Math.random() - 0.5) * 5000,
      -500 - Math.random() * 5000
    );
    asteroid.scale.setScalar(1 + Math.random());
    asteroid.rotation.set(Math.random() * Math.PI, Math.random() * Math.PI, 0);
    asteroid.velocity = new THREE.Vector3(
      (Math.random() - 0.5) * 0.1,
      (Math.random() - 0.5) * 0.1,
      -0.1
    );
    scene.add(asteroid);
    asteroids.push(asteroid);
  }
}

function updateAsteroids(deltaTime) {
  asteroids.forEach(asteroid => {
    asteroid.position.add(asteroid.velocity);
    if (Math.abs(asteroid.position.x) > 500) asteroid.position.x *= -0.9;
    if (Math.abs(asteroid.position.y) > 500) asteroid.position.y *= -0.9;
    if (asteroid.position.z > 200) {
      asteroid.position.z = -1000 - Math.random() * 500;
    }
  });
}

