// === PROGRESS BAR FUNCTIONS ===
function updateProgressBar() {
    if (!shuttle || !goal) return;

    // Calculate total journey distance (from start to goal)
    const totalDistance = Math.abs(goal.position.z);

    // Calculate current progress (how far the shuttle has traveled)
    const distanceTraveled = Math.abs(shuttle.position.z);

    // Calculate progress percentage
    const progressPercentage = (distanceTraveled / totalDistance) * 100;

    // Update progress bar fill
    const progressFill = document.getElementById('progress-fill');
    if (progressFill) {
        progressFill.style.width = `${progressPercentage}%`;
    }

    // Update shuttle indicator position
    const shuttleIndicator = document.getElementById('shuttle-indicator');
    if (shuttleIndicator) {
        // Calculate position (add 20px padding from left and right)
        const containerWidth = document.getElementById('progress-bar').offsetWidth;
        const position = 20 + ((containerWidth - 40) * progressPercentage / 100);
        shuttleIndicator.style.left = `${position}px`;
    }

    // Optional: Add color change as player gets closer to goal
    if (progressPercentage > 75) {
        progressFill.style.background = 'linear-gradient(90deg, #4fc3f7, #00e676)';
    } else if (progressPercentage > 50) {
        progressFill.style.background = 'linear-gradient(90deg, #4fc3f7, #76ff03)';
    } else if (progressPercentage > 25) {
        progressFill.style.background = 'linear-gradient(90deg, #4fc3f7, #ffeb3b)';
    }
}

// === UPDATE SCORE DISPLAY ===
function updateScoreDisplay() {
    const scoreValue = document.querySelector('.score-value');
    if (scoreValue) {
        scoreValue.textContent = score;

        // Add animation effect when score changes
        scoreValue.style.transform = 'scale(1.2)';
        scoreValue.style.color = '#ffff00';

        setTimeout(() => {
            scoreValue.style.transform = 'scale(1)';
            scoreValue.style.color = '#ffeb3b';
        }, 300);
    }
}

// === MODIFY EXISTING FUNCTIONS ===

// Update the updateShuttle function to call updateProgressBar
function updateShuttle() {
    if (!shuttle) return;

    shuttle.translateZ(-shuttleSpeed);

    const offset = new THREE.Vector3(0, 3, 10);
    camera.position.copy(shuttle.position.clone().add(offset));
    camera.lookAt(shuttle.position);

    if (shuttle.position.distanceTo(goal.position) < 10) {
        gameOver(true);
    }

    updateScoreDisplay();
    updateProgressBar(); // Add this line
    starField.position.copy(shuttle.position);
}

// Initialize the progress bar when the game starts
function initializeUI() {
    updateScoreDisplay();
    updateProgressBar();
    updateMissileCooldownIndicator(true);
}

// === MISSILE COOLDOWN INDICATOR ===
function updateMissileCooldownIndicator(ready) {
    const indicatorFill = document.querySelector('.indicator-fill');
    if (indicatorFill) {
        if (ready) {
            indicatorFill.style.width = '100%';
            indicatorFill.style.backgroundColor = '#4fc3f7';
        } else {
            indicatorFill.style.width = '0%';
            indicatorFill.style.backgroundColor = '#ff5252';

            // Animate the fill
            setTimeout(() => {
                indicatorFill.style.width = '100%';
                indicatorFill.style.backgroundColor = '#4fc3f7';
                indicatorFill.style.transition = 'width 0.3s ease, background-color 0.3s ease';
            }, 10);
        }
    }
}

// === MODIFY SHOOT MISSILE FUNCTION ===
function shootMissile() {
    if (missileCooldown || !shuttle) return;
    missileCooldown = true;

    // Update the missile cooldown indicator
    updateMissileCooldownIndicator(false);

    const missile = createMissile();
    missile.position.copy(shuttle.position);
    missile.quaternion.copy(shuttle.quaternion);
    missile.translateZ(2); // Slightly ahead of shuttle

    // Compute velocity toward aimPoint
    const direction = aimPoint.clone().sub(shuttle.position).normalize();

    missile.userData = {
        velocity: direction.clone().multiplyScalar(15),
        lifetime: 0
    };

    scene.add(missile);
    missiles.push(missile);

    setTimeout(() => {
        missileCooldown = false;
        updateMissileCooldownIndicator(true);
    }, 300);
}

// === INITIALIZE GAME ===
function initGame() {
    loadShuttle();
    loadAsteroids();
    initializeUI();
    animate();
}

// Call the initialization function
initGame();
