```javascript:main.js
// === PROGRESS BAR FUNCTIONS ===
function updateProgressBar() {
    // Check if shuttle and goal exist before using them
    if (!shuttle || !goal) {
        // If shuttle or goal don't exist yet, set progress to 0%
        const progressFill = document.getElementById('progress-fill');
        if (progressFill) {
            progressFill.style.width = '0%';
        }

        const shuttleIndicator = document.getElementById('shuttle-indicator');
        if (shuttleIndicator) {
            shuttleIndicator.style.left = '20px'; // Starting position
        }
        return;
    }

    // Calculate total journey distance (from start to goal)
    const totalDistance = Math.abs(goal.position.z);

    // Calculate current progress (how far the shuttle has traveled)
    const distanceTraveled = Math.abs(shuttle.position.z);

    // Calculate progress percentage
    const progressPercentage = (distanceTraveled / totalDistance) * 100;

    // Update progress bar fill
    const progressFill = document.getElementById('progress-fill');
    if (progressFill) {
        progressFill.style.width = `${progressPercentage}%`;

        // Optional: Add color change as player gets closer to goal
        if (progressPercentage > 75) {
            progressFill.style.background = 'linear-gradient(90deg, #4fc3f7, #00e676)';
        } else if (progressPercentage > 50) {
            progressFill.style.background = 'linear-gradient(90deg, #4fc3f7, #76ff03)';
        } else if (progressPercentage > 25) {
            progressFill.style.background = 'linear-gradient(90deg, #4fc3f7, #ffeb3b)';
        }
    }

    // Update shuttle indicator position
    const shuttleIndicator = document.getElementById('shuttle-indicator');
    if (shuttleIndicator) {
        const progressBar = document.getElementById('progress-bar');
        if (progressBar) {
            const containerWidth = progressBar.offsetWidth;
            const position = 20 + ((containerWidth - 40) * progressPercentage / 100);
            shuttleIndicator.style.left = `${position}px`;
        }
    }
}

// === UPDATE SCORE DISPLAY ===
function updateScoreDisplay() {
    const scoreDisplay = document.getElementById('distance-display');
    if (!scoreDisplay) return;

    if (!shuttle || !goal) {
        scoreDisplay.textContent = `Score: ${score} | Distance: N/A`;
        return;
    }

    scoreDisplay.textContent = `Score: ${score} | Distance: ${Math.abs(shuttle.position.z - goal.position.z).toFixed(0)}`;
}

// === INITIALIZE GAME ===
function initGame() {
    // Initialize UI first with default values
    initializeUIWithDefaults();

    // Then load 3D models
    loadShuttle();
    loadAsteroids();

    // Start animation loop
    animate();
}

// Initialize UI with default values (no dependency on shuttle)
function initializeUIWithDefaults() {
    // Initialize UI elements that don't depend on shuttle
    const scoreDisplay = document.getElementById('distance-display');
    if (scoreDisplay) {
        scoreDisplay.textContent = `Score: 0 | Distance: N/A`;
    }

    // Initialize progress bar with 0%
    const progressFill = document.getElementById('progress-fill');
    if (progressFill) {
        progressFill.style.width = '0%';
    }

    // Initialize missile cooldown indicator
    const indicatorFill = document.querySelector('.indicator-fill');
    if (indicatorFill) {
        indicatorFill.style.width = '100%';
        indicatorFill.style.backgroundColor = '#4fc3f7';
    }
}

// Replace the direct call to initializeUI at line 304 with:
document.addEventListener('DOMContentLoaded', function() {
    initGame();
});
```

This solution ensures that:
1. UI elements are initialized with default values that don't depend on the shuttle
2. The progress bar and score display are updated after the shuttle is loaded
3. All functions have proper null checks to handle cases where objects don't exist yet

