// === GOAL ===
let goal;
let goalLight; // Add a light to highlight the goal

function loadGoal() {
  // Create a placeholder sphere until the model loads
  const placeholderGeometry = new THREE.SphereGeometry(5, 16, 16);
  const placeholderMaterial = new THREE.MeshStandardMaterial({ 
    color: 0x00ff00,
    emissive: 0x00ff00,
    emissiveIntensity: 0.3
  });
  goal = new THREE.Mesh(placeholderGeometry, placeholderMaterial);
  goal.position.set(0, 0, -10000);
  scene.add(goal);

  // Add a point light to highlight the goal
  goalLight = new THREE.PointLight(0x00ff00, 2, 100);
  goalLight.position.copy(goal.position);
  scene.add(goalLight);

  // Load the 3D model
  const loader = new THREE.GLTFLoader();
  loader.load('assets/space_station.glb', (gltf) => {
    // Remove the placeholder
    scene.remove(goal);

    // Set up the new model
    goal = gltf.scene;
    goal.position.set(0, 0, -10000);
    goal.scale.set(20, 20, 20); // Adjust scale as needed for your model

    // Add rotation animation
    goal.userData.rotationSpeed = 0.005;

    // Add some emissive materials to make it glow
    goal.traverse(child => {
      if (child.isMesh) {
        // Store original material for reference
        child.originalMaterial = child.material.clone();

        // Create a new material with emissive properties
        child.material = new THREE.MeshStandardMaterial({
          color: child.material.color || 0xffffff,
          emissive: 0x00ff00,
          emissiveIntensity: 0.3,
          map: child.material.map,
          normalMap: child.material.normalMap,
          roughness: 0.5,
          metalness: 0.8
        });
      }
    });

    scene.add(goal);

    // Add a marker above the goal
    const markerGeometry = new THREE.ConeGeometry(2, 8, 16);
    const markerMaterial = new THREE.MeshBasicMaterial({ 
      color: 0x00ff00,
      transparent: true,
      opacity: 0.8
    });
    const marker = new THREE.Mesh(markerGeometry, markerMaterial);
    marker.position.set(0, 30, 0); // Position above the station
    marker.userData.floatSpeed = 0.05;
    marker.userData.floatRange = 5;
    marker.userData.initialY = marker.position.y;
    goal.add(marker);
  });
}

// Function to update the goal (rotation, effects, etc.)
function updateGoal(deltaTime) {
  if (!goal) return;

  // Rotate the goal
  if (goal.userData.rotationSpeed) {
    goal.rotation.y += goal.userData.rotationSpeed;
  }

  // Animate any child markers
  goal.traverse(child => {
    if (child.userData.floatSpeed) {
      // Create a floating effect
      child.position.y = child.userData.initialY + 
        Math.sin(Date.now() * 0.001 * child.userData.floatSpeed) * 
        child.userData.floatRange;
    }
  });

  // Pulse the goal light
  if (goalLight) {
    goalLight.intensity = 2 + Math.sin(Date.now() * 0.002) * 0.5;
    goalLight.position.copy(goal.position);
  }
}
