function updateDirectionIndicator() {
  if (!directionIndicator || !shuttle || !goal) return;

  // Calculate direction to goal
  const directionToGoal = goal.position.clone().sub(shuttle.position).normalize();

  // Convert to camera space
  const cameraWorldPos = new THREE.Vector3();
  camera.getWorldPosition(cameraWorldPos);

  // Calculate angle in the horizontal plane
  const angle = Math.atan2(directionToGoal.x, directionToGoal.z);

  // Position the indicator at the edge of the view
  directionIndicator.position.set(
    Math.sin(angle) * 5,
    0,
    -Math.cos(angle) * 5
  );

  // Rotate to point toward the goal
  directionIndicator.lookAt(
    cameraWorldPos.x + directionToGoal.x,
    cameraWorldPos.y + directionToGoal.y,
    cameraWorldPos.z + directionToGoal.z
  );

  // Hide when the goal is in view
  const goalScreenPosition = goal.position.clone().project(camera);
  if (Math.abs(goalScreenPosition.x) < 1 && 
      Math.abs(goalScreenPosition.y) < 1 && 
      goalScreenPosition.z < 1) {
    directionIndicator.visible = false;
  } else {
    directionIndicator.visible = true;
  }
}

