// === COLLISION DETECTION ===
function detectCollisions() {
  if (!shuttle || !gameActive) return;

  // Check shuttle-asteroid collisions
  for (let i = 0; i < asteroids.length; i++) {
    const ast = asteroids[i];
    if (shuttle.position.distanceTo(ast.position) < ast.scale.x * 2 + 2) {
      gameOver(false);
      return;
    }
  }

  // Check missile-asteroid collisions
  for (let i = missiles.length - 1; i >= 0; i--) {
    const missile = missiles[i];

    for (let j = asteroids.length - 1; j >= 0; j--) {
      const asteroid = asteroids[j];

      if (missile.position.distanceTo(asteroid.position) < asteroid.scale.x * 2 + 0.5) {
        scene.remove(missile);
        scene.remove(asteroid);
        missiles.splice(i, 1);
        asteroids.splice(j, 1);
        score++;
        createNewAsteroid();
        updateScoreDisplay();
        break; // Break inner loop after collision
      }
    }
  }
}

function createNewAsteroid() {
