// Create explosion effect
function createExplosion(position, color, size) {
  // Particles
  const particleCount = 20;
  const geometry = new THREE.BufferGeometry();
  const positions = new Float32Array(particleCount * 3);

  for (let i = 0; i < particleCount; i++) {
    const i3 = i * 3;
    positions[i3] = position.x;
    positions[i3 + 1] = position.y;
    positions[i3 + 2] = position.z;
  }

  geometry.setAttribute('position', new THREE.BufferAttribute(positions, 3));

  const material = new THREE.PointsMaterial({
    color: color,
    size: 1,
    transparent: true,
    opacity: 1
  });

  const particles = new THREE.Points(geometry, material);
  scene.add(particles);

  // Explosion light
  const light = new THREE.PointLight(color, 2, size * 10);
  light.position.copy(position);
  scene.add(light);

  // Animation
  const velocities = [];
  for (let i = 0; i < particleCount; i++) {
    velocities.push(
      (Math.random() - 0.5) * size,
      (Math.random() - 0.5) * size,
      (Math.random() - 0.5) * size
    );
  }

  // Animate explosion
  let explosionTime = 0;
  const explosionDuration = 1; // 1 second

  function animateExplosion() {
    explosionTime += 0.016; // Approx 60fps

    // Update particles
    const positions = particles.geometry.attributes.position.array;

    for (let i = 0; i < particleCount; i++) {
      const i3 = i * 3;
      positions[i3] += velocities[i3] * 0.1;
      positions[i3 + 1] += velocities[i3 + 1] * 0.1;
      positions[i3 + 2] += velocities[i3 + 2] * 0.1;
    }

    particles.geometry.attributes.position.needsUpdate = true;

    // Fade out
    const progress = explosionTime / explosionDuration;
    particles.material.opacity = 1 - progress;
    light.intensity = 2 * (1 - progress);

    if (explosionTime < explosionDuration) {
      requestAnimationFrame(animateExplosion);
    } else {
      scene.remove(particles);
      scene.remove(light);
    }
  }

  animateExplosion();
}
