  const material = new THREE.MeshStandardMaterial({
    color: 0xff0000,
    emissive: 0xff0000,
    emissiveIntensity: 1
  });

  const projectile = new THREE.Mesh(geometry, material);

  // Position projectile at UFO
  projectile.position.copy(ufo.position);

  // Calculate direction toward shuttle
  const direction = shuttle.position.clone().sub(ufo.position).normalize();

  // Add velocity to projectile
  projectile.userData = {
    velocity: direction.multiplyScalar(8), // Projectile speed
    fromUfo: true
  };

  scene.add(projectile);
  ufoProjectiles.push(projectile);

  // Add light trail effect
  const trail = new THREE.PointLight(0xff0000, 1, 10);
  projectile.add(trail);
}
