function createConstantRadiationGlow(ufo) {
  // Create a sphere geometry for the glow
  const glowGeometry = new THREE.SphereGeometry(10, 32, 32);

  // Create a shader material that creates a glow effect
  const glowMaterial = new THREE.ShaderMaterial({
    uniforms: {
      "c": { value: 0.2 },
      "p": { value: 4.0 },
      glowColor: { value: new THREE.Color(0x00ff00) },
      viewVector: { value: new THREE.Vector3() }
    },
    vertexShader: `
      uniform vec3 viewVector;
      uniform float c;
      uniform float p;
      varying float intensity;
      void main() {
        vec3 vNormal = normalize(normal);
        vec3 vNormel = normalize(viewVector);
        intensity = pow(c - dot(vNormal, vNormel), p);
        gl_Position = projectionMatrix * modelViewMatrix * vec4(position, 1.0);
      }
    `,
    fragmentShader: `
      uniform vec3 glowColor;
      varying float intensity;
      void main() {
        vec3 glow = glowColor * intensity;
        gl_FragColor = vec4(glow, intensity);
      }
    `,
    side: THREE.BackSide,
    blending: THREE.AdditiveBlending,
    transparent: true
  });

  // Create the glow mesh
  const glowMesh = new THREE.Mesh(glowGeometry, glowMaterial);

  // Add to UFO
  ufo.add(glowMesh);
  ufo.userData.radiationGlow = glowMesh;

  // Update the view vector in the animation loop
  const updateGlow = () => {
    if (glowMesh && camera) {
      const viewVector = new THREE.Vector3().subVectors(
        camera.position, 
        glowMesh.getWorldPosition(new THREE.Vector3())
      );
      glowMaterial.uniforms.viewVector.value = viewVector;
    }
    requestAnimationFrame(updateGlow);
  };

  updateGlow();

  // Pulse animation for the glow size
  let expanding = true;
  const minScale = 1.0;
  const maxScale = 1.2;
  const pulseDuration = 1.5; // seconds
  let startTime = performance.now();

  function pulseAnimation() {
    const elapsed = (performance.now() - startTime) / 1000; // seconds
    let progress = Math.min(elapsed / pulseDuration, 1);

    // Sine easing
    progress = expanding ? 
      Math.sin(progress * Math.PI/2) : // Ease out when expanding
      1 - Math.sin((1-progress) * Math.PI/2); // Ease in when contracting

    const currentScale = expanding ?
      minScale + (maxScale - minScale) * progress :
      maxScale - (maxScale - minScale) * progress;

    glowMesh.scale.set(currentScale, currentScale, currentScale);

