// Store the power-up models
let powerUpModels;

// Function to spawn a power-up
function spawnPowerUp(currentTime) {
  if (!shuttle || !powerUpModels) return;

  // Check if we should attempt to spawn
  if (currentTime - powerUpSettings.lastSpawnTime < powerUpSettings.spawnRate) return;
  powerUpSettings.lastSpawnTime = currentTime;

  // Check if we're at the maximum number of power-ups
  if (powerUps.length >= powerUpSettings.maxPowerUps) return;

  // Random chance to spawn
  if (Math.random() > powerUpSettings.spawnChance) return;

  // Choose a power-up type
  const type = Math.random() > 0.5 ? powerUpTypes.HEALTH : powerUpTypes.SHIELD;

  // Clone the appropriate model
  const powerUp = powerUpModels[type].clone();

  // Position in front of the shuttle but off to the side
  const shuttleForward = new THREE.Vector3(0, 0, -1).applyQuaternion(shuttle.quaternion);
  const offsetX = (Math.random() - 0.5) * 40;
  const offsetY = (Math.random() - 0.5) * 40;
  const offsetZ = -100 - Math.random() * 100; // Always ahead

  powerUp.position.copy(shuttle.position)
    .add(new THREE.Vector3(offsetX, offsetY, offsetZ));

  // Add metadata
  powerUp.userData = {
    type: type,
    rotationSpeed: 0.01 + Math.random() * 0.02,
    bobSpeed: 0.5 + Math.random() * 0.5,
    bobHeight: 0.2 + Math.random() * 0.3,
    originalY: powerUp.position.y,
    spawnTime: currentTime
  };

  // Add to scene and array
  scene.add(powerUp);
  powerUps.push(powerUp);

  // Add a point light to make it more visible
  const color = type === powerUpTypes.HEALTH ? 0xff0000 : 0x00aaff;
  const powerUpLight = new THREE.PointLight(color, 1, 20);
  powerUpLight.position.set(0, 0, 0);
  powerUp.add(powerUpLight);
}

// Update power-ups (rotation, bobbing, etc.)
function updatePowerUps(deltaTime, currentTime) {
  for (let i = powerUps.length - 1; i >= 0; i--) {
    const powerUp = powerUps[i];

    // Rotate the power-up
    powerUp.rotation.y += powerUp.userData.rotationSpeed;

    // Bob up and down
    const bobOffset = Math.sin(currentTime * 0.001 * powerUp.userData.bobSpeed) * powerUp.userData.bobHeight;
    powerUp.position.y = powerUp.userData.originalY + bobOffset;

    // Remove if too far behind the shuttle
    if (shuttle && powerUp.position.z > shuttle.position.z + 50) {
      scene.remove(powerUp);
      powerUps.splice(i, 1);
      continue;
    }

    // Check for collision with shuttle
    if (shuttle && powerUp.position.distanceTo(shuttle.position) < 5) {
      collectPowerUp(powerUp);
      scene.remove(powerUp);
      powerUps.splice(i, 1);
    }
  }
}

// Handle power-up collection
function collectPowerUp(powerUp) {
  const type = powerUp.userData.type;

  // Create a collection effect
  createCollectionEffect(powerUp.position.clone(), type);

  // Play collection sound
  playSound('powerup');

  // Apply power-up effect
  if (type === powerUpTypes.HEALTH) {
    // Health boost
    shuttleHealth = Math.min(shuttleHealth + 25, 100); // Add 25 health, max 100
    updateHealthDisplay();

    // Show message
    showMessage('Health Restored! +25', '#ff5555');
  } else if (type === powerUpTypes.SHIELD) {
    // Activate shield
    activateShield();

    // Show message
    showMessage('Radiation Shield Activated!', '#55aaff');
  }
}

// Create a visual effect when collecting a power-up
function createCollectionEffect(position, type) {
  // Create particles
  const particleCount = 20;
  const geometry = new THREE.BufferGeometry();
  const vertices = [];

  for (let i = 0; i < particleCount; i++) {
    vertices.push(
      (Math.random() - 0.5) * 2, // x
      (Math.random() - 0.5) * 2, // y
      (Math.random() - 0.5) * 2  // z
    );
  }

  geometry.setAttribute('position', new THREE.Float32BufferAttribute(vertices, 3));

  // Set color based on power-up type
  const color = type === powerUpTypes.HEALTH ? 0xff5555 : 0x55aaff;

  const material = new THREE.PointsMaterial({
    color: color,
    size: 0.5,
    transparent: true,
    opacity: 1
  });

  const particles = new THREE.Points(geometry, material);
  particles.position.copy(position);
  scene.add(particles);

  // Animate particles outward
  const startTime = performance.now();
  const duration = 1000; // 1 second

  function animateParticles() {
    const elapsed = performance.now() - startTime;
    const progress = Math.min(elapsed / duration, 1);

    // Scale particles outward
    particles.scale.set(1 + progress * 5, 1 + progress * 5, 1 + progress * 5);

    // Fade out
    particles.material.opacity = 1 - progress;

    if (progress < 1) {
      requestAnimationFrame(animateParticles);
    } else {
      scene.remove(particles);
      geometry.dispose();
      material.dispose();
    }
  }

  requestAnimationFrame(animateParticles);
}

// Shield functionality
let shieldActive = false;
let shieldEndTime = 0;
let shieldMesh = null;

function activateShield() {
  // Set shield status
  shieldActive = true;
  shieldEndTime = performance.now() + powerUpSettings.shieldDuration;

  // Create shield visual if it doesn't exist
  if (!shieldMesh) {
    // Create shield geometry
    const geometry = new THREE.SphereGeometry(4, 32, 32);
    const material = new THREE.MeshPhongMaterial({
      color: 0x00aaff,
      transparent: true,
      opacity: 0.3,
      emissive: 0x00aaff,
      emissiveIntensity: 0.3,
      side: THREE.DoubleSide
    });

    shieldMesh = new THREE.Mesh(geometry, material);

    // Add shield to shuttle
    if (shuttle) {
      shuttle.add(shieldMesh);
    }
  } else {
    // Make existing shield visible
    shieldMesh.visible = true;
  }

  // Pulse animation for shield
  const startTime = performance.now();

  function pulseShield() {
    if (!shieldActive || !shieldMesh) return;

    const elapsed = performance.now() - startTime;
    const pulseScale = 1 + 0.1 * Math.sin(elapsed * 0.005);

    shieldMesh.scale.set(pulseScale, pulseScale, pulseScale);

    // Continue animation if shield is still active
    if (performance.now() < shieldEndTime) {
      requestAnimationFrame(pulseShield);
    } else {
      deactivateShield();
    }
  }

  requestAnimationFrame(pulseShield);
}

function deactivateShield() {
  shieldActive = false;

  if (shieldMesh) {
    // Fade out animation
    const startOpacity = shieldMesh.material.opacity;
    const startTime = performance.now();
    const fadeDuration = 500; // 0.5 seconds

    function fadeOutShield() {
      const elapsed = performance.now() - startTime;
      const progress = Math.min(elapsed / fadeDuration, 1);

      shieldMesh.material.opacity = startOpacity * (1 - progress);

      if (progress < 1) {
        requestAnimationFrame(fadeOutShield);
      } else {
        shieldMesh.visible = false;
      }
    }

    requestAnimationFrame(fadeOutShield);
  }

  // Show message
  showMessage('Shield Deactivated', '#aaaaaa');
}

// Check shield status
function updateShield(currentTime) {
  if (shieldActive && currentTime > shieldEndTime) {
    deactivateShield();
  }
}

// Display a temporary message
function showMessage(text, color = '#ffffff', duration = 2000) {
