// Add a target indicator to the power-up
function addTargetIndicator(powerUp, type) {
  // Create a ring to indicate it's a target
  const color = type === powerUpTypes.HEALTH ? 0xff0000 : 0x00aaff;

  const ringGeometry = new THREE.RingGeometry(3, 3.5, 32);
  const ringMaterial = new THREE.MeshBasicMaterial({
    color: color,
    side: THREE.DoubleSide,
    transparent: true,
    opacity: 0.7
  });

  // Create three rings at different orientations
  const ring1 = new THREE.Mesh(ringGeometry, ringMaterial);
  ring1.rotation.x = Math.PI / 2;
  powerUp.add(ring1);

  const ring2 = new THREE.Mesh(ringGeometry, ringMaterial);
  ring2.rotation.y = Math.PI / 2;
  powerUp.add(ring2);

  const ring3 = new THREE.Mesh(ringGeometry, ringMaterial);
  powerUp.add(ring3);

  // Add pulsing animation to the rings
  const startTime = performance.now();

  function animateRings() {
    const elapsed = performance.now() - startTime;
    const scale = 1 + 0.2 * Math.sin(elapsed * 0.003);

    ring1.scale.set(scale, scale, scale);
    ring2.scale.set(scale, scale, scale);
    ring3.scale.set(scale, scale, scale);

    // Continue animation if power-up still exists
    if (powerUp.parent) {
      requestAnimationFrame(animateRings);
    }
  }

  requestAnimationFrame(animateRings);
}
