// Create an animated shield power-up
function createShieldPowerUp() {
  const group = new THREE.Group();

  // Core sphere (glowing blue)
  const coreSphere = new THREE.Mesh(
    new THREE.SphereGeometry(1.2, 24, 24),
    new THREE.MeshPhongMaterial({
      color: 0x00aaff,
      emissive: 0x0088ff,
      emissiveIntensity: 0.8,
      transparent: true,
      opacity: 0.7
    })
  );
  group.add(coreSphere);

  // Outer energy field (transparent blue)
  const outerSphere = new THREE.Mesh(
    new THREE.SphereGeometry(2, 24, 24),
    new THREE.MeshPhongMaterial({
      color: 0x55aaff,
      transparent: true,
      opacity: 0.3,
      wireframe: false,
      side: THREE.DoubleSide
    })
  );
  group.add(outerSphere);

  // Energy rings
  const ringCount = 3;
  const rings = [];

  for (let i = 0; i < ringCount; i++) {
    const ring = new THREE.Mesh(
      new THREE.TorusGeometry(2.5, 0.15, 16, 48),
      new THREE.MeshPhongMaterial({
        color: 0x00aaff,
        emissive: 0x0088ff,
        emissiveIntensity: 0.7,
        transparent: true,
        opacity: 0.8
      })
    );

    // Set initial rotation
    ring.rotation.x = Math.PI / 2 * i;
    ring.rotation.y = Math.PI / 3 * i;

    group.add(ring);
    rings.push(ring);
  }

  // Energy particles
  const particleCount = 50;
  const particleGeometry = new THREE.BufferGeometry();
  const particlePositions = new Float32Array(particleCount * 3);
  const particleSizes = new Float32Array(particleCount);

  for (let i = 0; i < particleCount; i++) {
    // Random position on a sphere
    const theta = Math.random() * Math.PI * 2;
    const phi = Math.acos(2 * Math.random() - 1);
    const radius = 1.5 + Math.random() * 1;

    particlePositions[i * 3] = radius * Math.sin(phi) * Math.cos(theta);
    particlePositions[i * 3 + 1] = radius * Math.sin(phi) * Math.sin(theta);
    particlePositions[i * 3 + 2] = radius * Math.cos(phi);

    particleSizes[i] = 0.1 + Math.random() * 0.2;
  }

  particleGeometry.setAttribute('position', new THREE.BufferAttribute(particlePositions, 3));
  particleGeometry.setAttribute('size', new THREE.BufferAttribute(particleSizes, 1));

  const particleMaterial = new THREE.PointsMaterial({
    color: 0x00aaff,
    size: 0.2,
    transparent: true,
    opacity: 0.8,
    blending: THREE.AdditiveBlending,
    sizeAttenuation: true
  });

  const particles = new THREE.Points(particleGeometry, particleMaterial);
  group.add(particles);

  // Add a point light in the center
  const light = new THREE.PointLight(0x00aaff, 1, 10);
  light.position.set(0, 0, 0);
  group.add(light);

  // Store animation properties
  group.userData = {
    type: powerUpTypes.SHIELD,
    coreSphere: coreSphere,
    outerSphere: outerSphere,
    rings: rings,
    particles: particles,
    light: light,
    rotationSpeed: 0.01 + Math.random() * 0.02,
    pulseSpeed: 0.5 + Math.random() * 0.5,
    originalScale: 1,
    particlePositions: particlePositions,
    particleGeometry: particleGeometry
  };

  return group;
}
