  const fadeInterval = setInterval(() => {
    opacity -= 0.1;
    flash.style.opacity = opacity.toString();

    if (opacity <= 0) {
      clearInterval(fadeInterval);
      item.removeChild(flash);
    }
  }, 50);
}

