// Modify the initGame function to not start immediately
function initGame() {
  // Load assets
  loadShuttle();
  loadAsteroids();
  loadPowerUpModels();

  // Initialize UI
  initInventoryUI();

  // Set game as initialized
  gameInitialized = true;

  // Start animation loop
  animate();
