// Modify the createAsteroids function to use the current difficulty
function createAsteroids() {
  // Clear existing asteroids
  asteroids.forEach(ast => scene.remove(ast));
  asteroids = [];

  // Get the current difficulty settings
  const settings = difficultySettings[difficultySettings.current];

  // Create new asteroids based on difficulty
  for (let i = 0; i < settings.asteroidCount; i++) {
    const asteroid = asteroidModels[Math.floor(Math.random() * asteroidModels.length)].clone();

    asteroid.position.set(
      (Math.random() - 0.5) * 5000,
      (Math.random() - 0.5) * 5000,
      -500 - Math.random() * 5000
    );

    asteroid.scale.setScalar(1 + Math.random());
    asteroid.rotation.set(Math.random() * Math.PI, Math.random() * Math.PI, 0);

    // Set velocity based on difficulty
    asteroid.velocity = new THREE.Vector3(
      (Math.random() - 0.5) * 0.1 * settings.asteroidSpeed,
      (Math.random() - 0.5) * 0.1 * settings.asteroidSpeed,
      -0.1 * settings.asteroidSpeed
    );

    scene.add(asteroid);
    asteroids.push(asteroid);
  }
}

// Modify the shootMissile function to use the current difficulty
function shootMissile() {
  if (missileCooldown || !shuttle) return;
  missileCooldown = true;

  const missile = createMissile();
  missile.position.copy(shuttle.position);
  missile.quaternion.copy(shuttle.quaternion);
  missile.translateZ(2); // Slightly ahead of shuttle

  // Compute velocity toward aimPoint
  const direction = aimPoint.clone().sub(shuttle.position).normalize();

  // Use missile speed from current difficulty
  const settings = difficultySettings[difficultySettings.current];

  missile.userData = {
    velocity: direction.clone().multiplyScalar(settings.missileSpeed),
    lifetime: 0
  };

  scene.add(missile);
  missiles.push(missile);

  setTimeout(() => missileCooldown = false, 300);
}

// Modify the resetGame function to apply difficulty settings
function resetGame() {
  score = 0;
  shuttleHealth = 100;

  if (shuttle) {
    shuttle.position.set(0, 0, 0);
    shuttle.rotation.set(0, 0, 0);
  }

  // Reset inventory
  inventory.health = 0;
  inventory.shield = 0;
  updateInventoryDisplay();

  // Clear existing objects
  asteroids.forEach(ast => scene.remove(ast));
  asteroids = [];

  missiles.forEach(m => scene.remove(m));
  missiles = [];

  powerUps.forEach(p => scene.remove(p));
  powerUps = [];

  // Deactivate shield
  if (shieldActive) {
    deactivateShield();
  }

  // Apply current difficulty settings
  applyDifficultySettings();

  // Create new asteroids based on current difficulty
  createAsteroids();

  // Update UI
  updateHealthDisplay();

  // Resume game
  gamePaused = false;
}

