  function updateJoystickPosition(e) {
    const touch = e.touches[0];
    const rect = joystickContainer.getBoundingClientRect();

    // Calculate position relative to joystick center
    let x = touch.clientX - rect.left - rect.width / 2;
    let y = touch.clientY - rect.top - rect.height / 2;

    // Limit to joystick radius
    const radius = rect.width / 2;
    const distance = Math.sqrt(x * x + y * y);

    if (distance > radius) {
      x = (x / distance) * radius;
      y = (y / distance) * radius;
    }

    // Update joystick position
    joystick.style.left = `calc(50% + ${x}px)`;
    joystick.style.top = `calc(50% + ${y}px)`;
    joystick.style.transform = 'translate(-50%, -50%)';

    // Update movement values (normalized -1 to 1)
    joystickMovement.x = x / radius;
    joystickMovement.y = y / radius;
  }

  // Return the movement object for use in game controls
  return joystickMovement;
}

