  // Submit score to leaderboard
  submitScore: async function(score, difficulty) {
    if (this.isGuest || !this.playerName) {
      console.log("Guest player, not submitting score");
      return { qualified: false };
    }

    try {
      const response = await fetch(`${this.apiUrl}/leaderboard`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          playerName: this.playerName,
          score: score,
          difficulty: difficulty
        })
      });

      const result = await response.json();
      return result;
    } catch (error) {
      console.error("Error submitting score:", error);
      return { error: "Failed to submit score", qualified: false };
    }
  },

  // Fetch leaderboard data
  fetchLeaderboard: async function() {
    try {
      const response = await fetch(`${this.apiUrl}/leaderboard`);
      const leaderboard = await response.json();
      return leaderboard;
    } catch (error) {
      console.error("Error fetching leaderboard:", error);
      return [];
