const { MongoClient } = require('mongodb');

// Replace with your MongoDB connection string
const uri = "mongodb://localhost:27017";
const client = new MongoClient(uri);

async function setupDatabase() {
  try {
    await client.connect();
    console.log("Connected to MongoDB");

    const db = client.db('asteroidGame');
    const leaderboardCollection = db.collection('leaderboard');

    // Create indexes
    await leaderboardCollection.createIndex({ score: -1 });
    console.log("Created index on score field");

    // Create a compound index for more complex queries
    await leaderboardCollection.createIndex({ difficulty: 1, score: -1 });
    console.log("Created compound index on difficulty and score");

    // Create a TTL index if you want entries to expire after a certain time (e.g., 1 year)
