      { playerName: "StarPilot", score: 4200, difficulty: "Medium", date: new Date() },
      { playerName: "CosmicRider", score: 3800, difficulty: "Hard", date: new Date() },
      { playerName: "AsteroidHunter", score: 3500, difficulty: "Easy", date: new Date() },
      { playerName: "GalacticHero", score: 3200, difficulty: "Medium", date: new Date() }
    ];

    // Only insert if collection is empty
    const count = await leaderboardCollection.countDocuments();
    if (count === 0) {
      await leaderboardCollection.insertMany(sampleData);
      console.log("Inserted sample leaderboard data");
    } else {
      console.log("Leaderboard already has data, skipping sample data insertion");
    }

    console.log("Database setup complete!");
  } catch (error) {
    console.error("Error setting up database:", error);
  } finally {
    await client.close();
  }
}

setupDatabase();
