app.get('/api/leaderboard', async (req, res) => {
  try {
    const { difficulty } = req.query;
    const collection = db.collection('leaderboard');

    let query = {};
    if (difficulty) {
      query.difficulty = difficulty;
    }

    const leaderboard = await collection.find(query)
      .sort({ score: -1 })
      .limit(10)
      .toArray();

    res.json(leaderboard);
  } catch (error) {
    console.error("Error fetching leaderboard:", error);
    res.status(500).json({ error: "Failed to fetch leaderboard" });
  }
});

app.get('/api/leaderboard/player/:name', async (req, res) => {
  try {
    const playerName = req.params.name;
    const collection = db.collection('leaderboard');

    const playerScores = await collection.find({ playerName })
      .sort({ score: -1 })
      .limit(5)
      .toArray();

    // Get player's rank for each score
    const enhancedScores = await Promise.all(playerScores.map(async (score) => {
      const rank = await collection.countDocuments({ score: { $gt: score.score } });
      return { ...score, rank: rank + 1 };
    }));

    res.json(enhancedScores);
  } catch (error) {
    console.error("Error fetching player scores:", error);
    res.status(500).json({ error: "Failed to fetch player scores" });
  }
});

app.get('/api/leaderboard/stats', async (req, res) => {
  try {
    const collection = db.collection('leaderboard');

    const totalPlayers = await collection.distinct('playerName').then(names => names.length);
    const totalScores = await collection.countDocuments();

    const difficultyStats = await collection.aggregate([
      { $group: { _id: "$difficulty", count: { $sum: 1 }, avgScore: { $avg: "$score" } } }
    ]).toArray();

    const highestScore = await collection.find().sort({ score: -1 }).limit(1).toArray();

    res.json({
      totalPlayers,
      totalScores,
      difficultyStats,
      highestScore: highestScore[0] || null
    });
  } catch (error) {
    console.error("Error fetching leaderboard stats:", error);
    res.status(500).json({ error: "Failed to fetch leaderboard stats" });
  }
});

app.post('/api/leaderboard', async (req, res) => {
  try {
    const { playerName, score, difficulty } = req.body;

    if (!playerName || score === undefined || !difficulty) {
      return res.status(400).json({ error: "Missing required fields" });
    }

    const collection = db.collection('leaderboard');

    // Get current leaderboard for this difficulty
    const leaderboard = await collection.find({ difficulty })
      .sort({ score: -1 })
      .limit(10)
      .toArray();

    // Check if score qualifies for leaderboard
    const lowestScore = leaderboard.length === 10 ? leaderboard[9].score : 0;

    if (score > lowestScore || leaderboard.length < 10) {
      // Add new score
      const result = await collection.insertOne({
        playerName,
        score,
        difficulty,
        date: new Date()
      });

      // Get player's rank
      const rank = await collection.countDocuments({ 
        difficulty,
        score: { $gt: score } 
      }) + 1;

      res.status(201).json({ 
        message: "Score added to leaderboard",
        qualified: true,
        rank
      });
    } else {
      // Get player's rank anyway
      const rank = await collection.countDocuments({ 
        difficulty,
        score: { $gt: score } 
      }) + 1;

      res.json({ 
        message: "Score did not qualify for top 10 leaderboard",
        qualified: false,
        rank
      });
    }
  } catch (error) {
    console.error("Error adding score:", error);
    res.status(500).json({ error: "Failed to add score" });
  }
});

