    messageTitle.style.fontSize = '24px';

    const messageText = document.createElement('p');
    messageText.style.margin = '0 0 20px 0';
    messageText.style.fontSize = '18px';
    messageText.style.lineHeight = '1.5';

    // Set content based on result
