    try {
      // First, check if score qualifies for top 10
      const leaderboard = await this.fetchLeaderboard(difficulty);
      const lowestScore = leaderboard.length === 10 ? leaderboard[9].score : 0;
      const qualified = score > lowestScore || leaderboard.length < 10;

      // Calculate rank
      let rank = 1;
      for (const entry of leaderboard) {
        if (entry.score > score) rank++;
      }

      // If qualified or we want to store all scores, insert it
      if (qualified) {
