      if (difficulty) {
        query = this.db.collection('leaderboard')
          .where('difficulty', '==', difficulty)
          .orderBy('score', 'desc')
          .limit(10);
      }

      const snapshot = await query.get();
      const leaderboard = [];

      snapshot.forEach(doc => {
        leaderboard.push({
          id: doc.id,
          ...doc.data()
        });
      });

      return leaderboard;
    } catch (error) {
      console.error("Error fetching leaderboard:", error);
      return [];
    }
  }
};
