let gameStarted = false;
let gamePaused = true;

function startGame(difficulty) {
  gameStarted = true;
  gamePaused = false;

  // Apply difficulty settings
  if (difficulty === 'easy') {
    // Fewer asteroids, slower speed
    while (asteroids.length > 100) {
      scene.remove(asteroids[asteroids.length - 1]);
      asteroids.pop();
    }
    shuttleSpeed = 1.5;
  } else if (difficulty === 'medium') {
    // Default settings
    while (asteroids.length > 200) {
      scene.remove(asteroids[asteroids.length - 1]);
      asteroids.pop();
    }
    while (asteroids.length < 200) {
      createNewAsteroid();
    }
    shuttleSpeed = 2;
  } else if (difficulty === 'hard') {
    // More asteroids, faster speed
